package com.wishill.wishill.adapter;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.RelativeLayout;import com.bumptech.glide.Glide;import com.bumptech.glide.load.engine.DiskCacheStrategy;import com.wishill.wishill.R;import com.wishill.wishill.api.recommendedColleges.getCollegeGallery.CollegeGalleryListData;import com.wishill.wishill.api.recommendedColleges.getGallery.GalleryListData;import com.wishill.wishill.utilities.APILinks;import java.util.List;public class GalleryListAdapter extends RecyclerView.Adapter<GalleryListAdapter.ViewHolder> {    List<GalleryListData> galleryDataList;    public ItemClickAdapterListener onClickListener;    String imagePath;    String video = "1";    public GalleryListAdapter(String imagePath, List<GalleryListData> galleryDataList, ItemClickAdapterListener onClickListener) {        this.galleryDataList = galleryDataList;        this.onClickListener = onClickListener;        this.imagePath=imagePath;    }    @Override    public ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View v = LayoutInflater.from(parent.getContext()).inflate(R.layout.video_gallery_item, parent, false);        ViewHolder viewHolder = new ViewHolder(v);        return viewHolder;    }    @Override    public void onBindViewHolder(ViewHolder holder, final int position) {        String previewUrl = "";        if (galleryDataList.get(position).getCategoryID().equals(video)) {            previewUrl = "https://img.youtube.com/vi/" + galleryDataList.get(position).getVideo() + "/0.jpg";            Log.d("videoUrl",previewUrl);            holder.playButton.setVisibility(View.VISIBLE);        } else {            previewUrl = APILinks.IMAGE_LINK + imagePath + galleryDataList.get(position).getGalleryItem();            holder.playButton.setVisibility(View.GONE);        }        Glide.with(holder.thumbnail.getContext()).load(previewUrl)                .thumbnail(0.5f)                .crossFade()                .diskCacheStrategy(DiskCacheStrategy.ALL)                .into(holder.thumbnail);        /*Glide.with(holder.thumbnail.getContext()).load(APILinks.IMAGE_LINK+imagePath+ galleryDataList.get(position).getItem())                .crossFade()                .thumbnail(0.5f)                .diskCacheStrategy(DiskCacheStrategy.ALL)                .into(holder.thumbnail);*/        holder.llMain.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                onClickListener.itemClick(v,position);            }        });    }    @Override    public int getItemCount() {        return galleryDataList.size();    }    public static class ViewHolder extends RecyclerView.ViewHolder {        ImageView thumbnail;        RelativeLayout llMain;        RelativeLayout playButton;        public ViewHolder(View itemView) {            super(itemView);            thumbnail = itemView.findViewById(R.id.thumbnail);            llMain=itemView.findViewById(R.id.ll_main);            playButton = itemView.findViewById(R.id.btn_play);        }    }    public interface ItemClickAdapterListener {        void itemClick(View v, int position);    }}